// import the React and ReactDOM libraries
import React from 'react'
import ReactDOM from 'react-dom'
import faker from 'faker'
import CommentDetail from './CommentDetail'

// create a react component
const App=()=>{
    return (
        <div className="ui container comments">
            <CommentDetail author="Sam" time="today at 2 PM" content="nice blog post" avatar={faker.image.avatar()}/>
            <CommentDetail author="alex" time="today at 7 AM" content="great writing skills" avatar={faker.image.avatar()}/>
            <CommentDetail author="julie" time="yesterday at 5 PM" content="keep it up" avatar={faker.image.avatar()}/>
        </div>
    )
}
// get this component to show on the page
ReactDOM.render(
    <App/>,
    document.querySelector("#root")
)
