import React from 'react';
import axios from 'axios';
import GameCard from "./GameCard";


class App extends React.Component {
    state = {games: [],cards:[]}


    componentDidMount = () => {
        axios.get('https://api.sportsdata.io/v3/soccer/scores/json/GamesByDate/2019-10-05', {
            params: {
                key: '46754a75f00b4c7996afa643e4ddbce8'
            }
        }).then(result => {
            console.log(result)
            const x = result.data.map((game) => {
                return ([game.HomeTeamName, game.AwayTeamName,game.DateTime])
            })
            console.log(x)
            this.setState({games: x})
            const matches=[]
            for (var i=0;i<10;i++){
                matches.push(<GameCard fixtures={this.state.games[i]}/>)
            }
            this.setState({cards:matches})
        })
    }

    render() {

        return (
            <div>
                {this.state.cards}
            </div>
        )
    }
}

export default App
