import React from 'react';
import axios from 'axios'


class App extends React.Component{
    state = {games:[]}
    componentDidMount() {
        axios.get('https://api.sportsdata.io/v3/soccer/scores/json/GamesByDate/2019-10-02',{
            params: {
                key: '46754a75f00b4c7996afa643e4ddbce8'
            }
        }).then(result => {
            console.log(result)
            const x=result.data.map((game) => {
                return ({gameId:game.GameId,teamA:game.HomeTeamName,teamB:game.AwayTeamName})
            })
            this.setState({games:x})
        })
        }



    render() {
        return <div>{this.state.games}  </div>
    }


}

export default App
