import React from 'react';
import axios from 'axios';
import GameCard from "./GameCard";


class App extends React.Component {
    state = {games: []}

    renderContent = () => {
        this.state.games.map((fixtures) => {
            return <GameCard fixtures={fixtures}/>
        })
    }

    componentDidMount() {
        axios.get('https://api.sportsdata.io/v3/soccer/scores/json/GamesByDate/2019-10-02', {
            params: {
                key: '46754a75f00b4c7996afa643e4ddbce8'
            }
        }).then(result => {
            console.log(result)
            const x = result.data.map((game) => {
                return ([game.HomeTeamName, game.AwayTeamName])
            })
            console.log(x)
            this.setState({games: x})
        })
    }


    render() {
        return (
            <div>
                {this.renderContent}
            </div>
        )
    }
}

export default App
